import React, { useState } from 'react';
import axios from 'axios';
import Dropzone from 'react-dropzone';
import './App.css';

// Dynamically import all images from the 'images' folder
const importAll = (requireContext) => requireContext.keys().map(requireContext);
const images = importAll(require.context('./images', false, /\.(png|jpe?g|svg)$/));

const App = () => {
  const [image, setImage] = useState(null);
  const [foodName, setFoodName] = useState('');

  const handleDrop = (acceptedFiles) => {
    const file = acceptedFiles[0];
    setImage(URL.createObjectURL(file));
    predictFood(file);
  };

  const predictFood = (file) => {
    const formData = new FormData();
    formData.append('file', file);

    axios.post('http://127.0.0.1:5000/predict', formData)
      .then(response => {
        console.log(response.data); // Debug: log response data
        setFoodName(response.data.prediction);
      })
      .catch(error => console.error('Error uploading image:', error));
  };

  const handleImageClick = (src) => {
    fetch(src)
      .then(res => res.blob())
      .then(blob => {
        const file = new File([blob], 'food.jpg', { type: blob.type });
        setImage(URL.createObjectURL(file));
        predictFood(file);
      });
  };

  return (
    <div className="App">
      <header className="App-header">
        <nav>
          <ul className="navbar">
            <li><a href="#about">About</a></li>
            <li><a href="#food-detection">Food Detection</a></li>
            <li><a href="#common-foods">Common Foods</a></li>
            <li><a href="#korean-foods">Korean Foods</a></li>
          </ul>
        </nav>
        <h1>Food Recognition</h1>
      </header>
      <main>
        <section id="food-detection">
          <Dropzone onDrop={handleDrop}>
            {({ getRootProps, getInputProps }) => (
              <div {...getRootProps()} className="dropzone">
                <input {...getInputProps()} />
                <p>Drag & drop an image, or click to select one</p>
              </div>
            )}
          </Dropzone>
          {image && <img src={image} alt="Uploaded food" className="uploaded-image" />}
          {foodName && <h2>Recognized Food: {foodName}</h2>}
        </section>
        <section id="common-foods">
          <h2>Common Foods</h2>
          {/* Add common foods content here */}
        </section>
        <section id="korean-foods">
          <h2>Korean Foods</h2>
          <div className="food-gallery">
            {images.map((img, index) => (
              <img 
                key={index} 
                src={img.default} 
                alt={`Korean Food ${index + 1}`} 
                className="food-image" 
                onClick={() => handleImageClick(img.default)} 
              />
            ))}
          </div>
        </section>
        <section id="about">
          <h2>About</h2>
          {/* Add about content here */}
        </section>
      </main>
    </div>
  );
};

export default App;
