import React, { useState } from 'react';
import axios from 'axios';
import Dropzone from 'react-dropzone';
import './App.css';
import koreanFood1 from './k1.jpg';
import koreanFood2 from './k2.jpg';
import koreanFood3 from './k3.jpg';
import koreanFood4 from './k4.jpg';
import koreanFood5 from './k5.jpg';
import koreanFood6 from './k6.jpg';
import koreanFood7 from './k7.jpg';
import koreanFood8 from './k8.jpg';
import koreanFood9 from './k9.jpg';
import koreanFood10 from './k10.jpg';
import koreanFood11 from './k11.jpg';
import koreanFood12 from './k12.jpg';

const App = () => {
  const [image, setImage] = useState(null);
  const [foodName, setFoodName] = useState('');

  const handleDrop = (acceptedFiles) => {
    setImage(acceptedFiles[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('file', image);

    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setFoodName(response.data.foodName);
    } catch (error) {
      console.error('Error uploading image:', error);
    }
  };

  return (
    <div className="container">
      <h1>Korean Food Image Classifier</h1>
      <Dropzone onDrop={handleDrop} accept="image/*">
        {({ getRootProps, getInputProps }) => (
          <div {...getRootProps({ className: 'dropzone' })}>
            <input {...getInputProps()} />
            {image ? (
              <img src={URL.createObjectURL(image)} alt="Selected" className="selected-image" />
            ) : (
              <p>Drag and drop an image here, or click to select one</p>
            )}
          </div>
        )}
      </Dropzone>
      <button onClick={handleUpload} className="upload-button">Upload</button>
      {foodName && (
        <div className="result-container">
          <h2>Predicted Food Name: {foodName}</h2>
        </div>
      )}
      <div className="image-grid">
        <img src={koreanFood1} alt="Korean Food 1" />
        <img src={koreanFood2} alt="Korean Food 2" />
        <img src={koreanFood3} alt="Korean Food 3" />
        <img src={koreanFood4} alt="Korean Food 4" />
        <img src={koreanFood5} alt="Korean Food 5" />
        <img src={koreanFood6} alt="Korean Food 6" />
        <img src={koreanFood7} alt="Korean Food 7" />
        <img src={koreanFood8} alt="Korean Food 8" />
        <img src={koreanFood9} alt="Korean Food 9" />
        <img src={koreanFood10} alt="Korean Food 10" />
        <img src={koreanFood11} alt="Korean Food 11" />
        <img src={koreanFood12} alt="Korean Food 12" />
      </div>
    </div>
  );
};

export default App;
